

  import { createContext, useState } from "react";
import toast from "react-hot-toast";

export const TeachersContext = createContext();

export const TeachersProvider = ({ children }) => {
  const [teachers, setTeachers] = useState([
    {id:1, name:"Nurillo", surname:"Suyunboyev", age:0, region:"Navoiy",  img:"data:image/jpeg;base64,"},
    {id:2, name:"Ahmadjon", surname:"Murodullayev", age:0, region:"O'zbekiston", img:"data:image/jpeg;base64,"},
  ]);

  // CREATE
   function addTeacher(newTeacher){
  const updated = [...teachers, newTeacher];
  setTeachers(updated);
}

  // DELETE
  function deleteTeacher(id) {
    const deleted = [...teachers];
    const index = deleted.findIndex(user => user.id === id);
    
      deleted.splice(index, 1);
      setTeachers(deleted);
      toast.success("Student deleted!");
  }


  return (
    <TeachersContext.Provider
      value={{ teachers, setTeachers, addTeacher, deleteTeacher}}
    >
      {children}
    </TeachersContext.Provider>
  );
};
